import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { useContext, useEffect, useReducer } from "react";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Card from "react-bootstrap/Card";
import ListGroup from "react-bootstrap/ListGroup";
import Badge from "react-bootstrap/Badge";
import Button from "react-bootstrap/Button";
import Rating from "../components/Rating";
import { Helmet } from "react-helmet-async";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import { getError } from "../utils";
import { Store } from "../Store";

const reducer = (state, action) => {
    switch (action.type) {
        case "FETCH_REQUEST":
            return {...state, loading: true };
        case "FETCH_SUCCESS":
            return {...state, product: action.payload, loading: false };
        case "FETCH_FAIL":
            return {...state, loading: false, error: action.payload };
        default:
            return state;
    }
};

function ProductScreen() {
    const navigate = useNavigate();
    const params = useParams();
    const { slug } = params;
    const [{ loading, error, product }, dispatch] = useReducer(reducer, {
        product: [],
        loading: true,
        error: "",
    });
    useEffect(() => {
        const fetchData = async() => {
            dispatch({ type: "FETCH_REQUEST" });
            try {
                const result = await axios.get(`/api/products/slug/${slug}`);
                dispatch({ type: "FETCH_SUCCESS", payload: result.data });
            } catch (err) {
                dispatch({ type: "FETCH_FAIL", payload: getError(err) });
            }
        };
        fetchData();
    }, [slug]);

    const { state, dispatch: ctxDispatch } = useContext(Store);
    const { cart } = state;
    const addToCartHandler = async() => {
        const existItem = cart.cartItems.find((x) => x._id === product._id);
        const quantity = existItem ? existItem.quantity + 1 : 1;
        const { data } = await axios.get(`/api/products/${product._id}`);
        if (data.countInStock < quantity) {
            window.alert("Przepraszamy, produkt nie znajduje siÄ™ w magazynie");
            return;
        }
        ctxDispatch({
            type: "CART_ADD_ITEM",
            payload: {...product, quantity },
        });
        navigate("/cart");
    };

    return loading ? ( <
        LoadingBox / >
    ) : error ? ( <
        MessageBox variant = "danger" > { error } < /MessageBox>
    ) : ( <
        div >
        <
        Row >
        <
        Col md = { 6 } >
        <
        img className = "img-large"
        src = { product.image }
        alt = { product.name } >
        < /img> <
        /Col> <
        Col md = { 6 } >
        <
        div className = "star-1" > < label > < input onChange = { useRadio }
        id = "first"
        name = "first"
        value = "first"
        type = "radio"
        checked = { radio === 'first' }
        /></label > < img src = { imgGoldStar } > < /img><span>1.0</span > < /div> <
        div className = "star-2" > < label > < input onChange = { useRadio }
        id = "second"
        name = "second"
        value = "second"
        type = "radio"
        checked = { radio === 'second' }
        /></label > < img src = { imgGoldStar } > < /img><img src={imgGoldStar}></img > < span > 2.0 < /span></div >
        <
        div className = "star-3" > < label > < input onChange = { useRadio }
        id = "third"
        name = "third"
        value = "third"
        type = "radio"
        checked = { radio === 'third' }
        /></label > < img src = { imgGoldStar } > < /img><img src={imgGoldStar}></img > < img src = { imgGoldStar } > < /img><span>3.0</span > < /div> <
        div className = "star-4" > < label > < input onChange = { useRadio }
        id = "four"
        name = "four"
        value = "four"
        type = "radio"
        checked = { radio === 'four' }
        /></label > < img src = { imgGoldStar } > < /img><img src={imgGoldStar}></img > < img src = { imgGoldStar } > < /img><img src={imgGoldStar}></img > < span > 4.0 < /span></div >
        <
        div className = "star-5" > < label > < input onChange = { useRadio }
        id = "five"
        name = "five"
        value = "five"
        type = "radio"
        checked = { radio === 'five' }
        /></label > < img src = { imgGoldStar } > < /img><img src={imgGoldStar}></img > < img src = { imgGoldStar } > < /img><img src={imgGoldStar}></img > < img src = { imgGoldStar } > < /img><span>5.0</span > < /div> <
        button className = "btn btn-primary"
        onClick = { markBook } > WyÅ›lij ocenÄ™ < /button> {
            showInfoBook === true ? < > < div className = "alert alert-success" > KsiÄ…Å¼ka zostaÅ‚a oceniona < /div><button className="btn btn-primary" onClick={displayAlert}>Ok</button > < /> : null}

